import { useState, useEffect } from 'react';

export default function useForm(initial = {}) {
  const [inputs, setInputs] = useState(initial);

  const handleChange = (e) => {
    const { value, name, type } = e.target;
    console.log(type);
    setInputs({
      ...inputs,
      [name]: value,
    });
  };

  return {
    inputs,
    handleChange,
  };
}
