import { useState } from 'react';
import useForm from '../lib/useForm';
import Form from './styles/Form';

const CreateProduct = () => {
  const { inputs, handleChange, clearForm, resetForm } = useForm({
    image: '',
    name: '',
    price: '',
    description: '',
  });
  console.log(inputs);
  return (
    <Form>
      <fieldset>
        <label htmlFor='image'>
          Image
          <input type='file' id='image' name='image' onChange={handleChange} />
        </label>
        <label htmlFor='name'>
          Name
          <input
            type='text'
            id='name'
            name='name'
            value={inputs.name}
            onChange={handleChange}
          />
        </label>
        <label htmlFor='price'>
          Price
          <input
            type='number'
            id='price'
            name='price'
            value={inputs.price}
            onChange={handleChange}
          />
        </label>
        <button type='submit' onClick={clearForm}>
          + Add product
        </button>
      </fieldset>
    </Form>
  );
};

export default CreateProduct;
